import '../App.css';

let our = '우리팀화이팅';

let mystyle = 'App-link';

const styleA = {color:'blue', fontSize:'22px'};

function msg(){return "오늘도 행복";}

function App(){
    return(
        <div className='App'>
            <h2 className='{mystyle}'>{our}</h2> 
            {/* 작은따옴표 빼야 적용됨 */}
            <h2 className={mystyle}>{our}</h2>
            <h2 className='App-link'>{our}</h2>
            <hr/>
            <h4>msg()</h4>
            {/* 함수, 변수 등 실행하려면 중괄호로 묶어줘야함 */}
            <h4 style={styleA}>{msg()}</h4>
            <h4 style={styleA}>{msg()}</h4>
        </div>
    );
}

export default App;
