/* 
    MyHeader 컴포넌트

    <header>
        <h1> 제목 </h1>
        <p> 내용 </p>
    </header>


*/


function MyHeader(props){
    console.log(props)

    // props.titile = '제목변경'; props는 read-only라서 변경안됨
    return(
        <header>
            <h1>{props.title}</h1>
            <p>{props.content}</p>
        </header>
    );

}



function App(){
    return(
        <div className="App">
            <MyHeader title='자바' content='즐거운 자바'></MyHeader>
            <MyHeader title='파이썬' content='재미있는 파이썬'></MyHeader>
        </div>
    );
}

export default App;
